package com.aidost.aigateway.controller;
import com.aidost.aigateway.dto.AiRequests.*;import com.aidost.aigateway.service.AiGatewayService;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.*;
@RestController @RequestMapping("/ai-gateway") @RequiredArgsConstructor public class AiGatewayController { private final AiGatewayService service; @PostMapping("/explain-dsa") public String explain(@RequestBody DsaExplainRequest request){ return service.explainDsaProblem(request);} @PostMapping("/generate-mock-questions") public String mock(@RequestBody GenerateMockQuestionRequest request){ return service.generateMockQuestions(request);} @PostMapping("/evaluate-mock-answer") public String eval(@RequestBody EvaluateMockAnswerRequest request){ return service.evaluateMockAnswer(request);} }
