package com.aidost.project.controller;
import com.aidost.project.model.*;import com.aidost.project.repository.*;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.*;import java.util.List;
@CrossOrigin(origins = {"http://localhost:5173", "http://localhost:3000"})
@RestController @RequiredArgsConstructor public class ProjectExperienceController { private final ProjectRepository projectRepo; private final ExperienceRepository expRepo; @PostMapping("/project/me") public Project addProject(@RequestBody Project p){ p.setUserId(1L); return projectRepo.save(p);} @GetMapping("/project/me") public List<Project> projects(){ return projectRepo.findAllByUserId(1L);} @PostMapping("/experience/me") public Experience addExp(@RequestBody Experience e){ e.setUserId(1L); return expRepo.save(e);} @GetMapping("/experience/me") public List<Experience> exp(){ return expRepo.findAllByUserId(1L);} }
